// 评分项定义（键、中文名）
const CRITERIA = [
  { key: "semanticAdherence", label: "语义贴合度 (Semantic Adherence)" },
  { key: "physicalCommonsense", label: "物理常识性 (Physical Commonsense)" },
  { key: "videoQuality", label: "视频质量 (Video Quality)" },
];

const GROUP_SIZE = 8;
const GROUP_COUNT = Math.ceil(videoList.length / GROUP_SIZE);
const LETTERS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

// ====== 状态 ======
let idx = 0; // 当前组索引
// ratings: [{orders: {semanticAdherence: [...videoIds], ...}}]
const ratings = Array.from({ length: GROUP_COUNT }, () => ({}));
let currentOrders = {};

// ====== DOM 引用 ======
const cover = document.getElementById("cover");
const page = document.getElementById("page");
const done = document.getElementById("done");
const startBtn = document.getElementById("startBtn");
const backBtn = document.getElementById("backBtn");
const nextBtn = document.getElementById("nextBtn");
const downloadBtn = document.getElementById("downloadBtn");
const counter = document.getElementById("counter");
const videoGrid = document.getElementById("videoGrid");
const sortArea = document.getElementById("sortArea");
const errorBox = document.getElementById("errorBox");

// ====== 事件绑定 ======
startBtn.addEventListener("click", () => {
  if (!videoList.length) {
    alert("videoList 为空，请先运行 process.py 生成列表。");
    return;
  }
  idx = 0;
  renderPage();
});

backBtn.addEventListener("click", () => {
  if (idx > 0) {
    collectCurrentPage(); // 允许未满也保存，方便返回修正
    idx--;
    renderPage();
  }
});

nextBtn.addEventListener("click", () => {
  if (!collectCurrentPage()) {
    errorBox.style.display = "block";
    return;
  }
  if (idx < GROUP_COUNT - 1) {
    idx++;
    renderPage();
  } else {
    // 完成
    page.style.display = "none";
    done.style.display = "block";
  }
});

downloadBtn.addEventListener("click", downloadCSV);

// ====== 渲染单页（每组 8 个视频排序） ======
function renderPage() {
  cover.style.display = "none";
  done.style.display = "none";
  page.style.display = "block";

  const start = idx * GROUP_SIZE;
  const groupVideos = videoList.slice(start, start + GROUP_SIZE);
  const N = GROUP_COUNT;
  counter.textContent = `第 ${idx + 1} / ${N} 组`;

  renderVideoGrid(groupVideos);

  const saved = ratings[idx];
  currentOrders = saved?.orders ? structuredClone(saved.orders) : {};
  CRITERIA.forEach((c) => {
    if (!currentOrders[c.key]) {
      currentOrders[c.key] = groupVideos.map((_, i) => i);
    }
  });
  renderSortLists(groupVideos);

  backBtn.style.visibility = idx === 0 ? "hidden" : "visible";
  nextBtn.textContent = idx === N - 1 ? "提交并完成" : "下一组";
  errorBox.style.display = "none";
}

// ====== 渲染视频网格 ======
function renderVideoGrid(groupVideos) {
  videoGrid.innerHTML = "";
  groupVideos.forEach((v, i) => {
    const card = document.createElement("div");
    card.className = "video-card";
    const title = document.createElement("h4");
    title.textContent = `#${displayLabel(i)}`;
    const video = document.createElement("video");
    video.controls = true;
    video.src = v.src;
    card.appendChild(title);
    card.appendChild(video);
    videoGrid.appendChild(card);
  });
}

// ====== 渲染排序列表 ======
function renderSortLists(groupVideos) {
  sortArea.innerHTML = "";
  CRITERIA.forEach((c) => {
    const section = document.createElement("div");
    section.className = "sort-section";
    const title = document.createElement("h4");
    title.textContent = c.label;
    section.appendChild(title);

    const list = document.createElement("ul");
    list.className = "sort-list";
    const order = currentOrders[c.key];
    order.forEach((idxInGroup, pos) => {
      const li = document.createElement("li");
      li.className = "sort-item";

      const label = document.createElement("div");
      label.className = "sort-label";
      const video = groupVideos[idxInGroup];
      label.textContent = `${pos + 1}. #${displayLabel(idxInGroup)}`;

      const actions = document.createElement("div");
      actions.className = "sort-actions";
      const up = document.createElement("button");
      up.textContent = "上移";
      up.disabled = pos === 0;
      up.addEventListener("click", () => moveItem(c.key, pos, -1));
      const down = document.createElement("button");
      down.textContent = "下移";
      down.disabled = pos === order.length - 1;
      down.addEventListener("click", () => moveItem(c.key, pos, 1));

      actions.appendChild(up);
      actions.appendChild(down);

      li.appendChild(label);
      li.appendChild(actions);
      list.appendChild(li);
    });

    section.appendChild(list);
    sortArea.appendChild(section);
  });
}

function moveItem(key, from, delta) {
  const order = currentOrders[key];
  const to = from + delta;
  if (to < 0 || to >= order.length) return;
  [order[from], order[to]] = [order[to], order[from]];
  const start = idx * GROUP_SIZE;
  const groupVideos = videoList.slice(start, start + GROUP_SIZE);
  renderSortLists(groupVideos);
}

function displayLabel(idxInGroup) {
  return LETTERS[idxInGroup] || `#${idxInGroup + 1}`;
}

function shortName(key) {
  if (key === "semanticAdherence") return "SA_rank";
  if (key === "physicalCommonsense") return "PC_rank";
  if (key === "videoQuality") return "VQ_rank";
  return key;
}

function modelNameFromId(videoId) {
  if (!videoId) return "";
  const parts = videoId.split("/");
  const name = parts.length > 1 ? parts[0] : videoId;
  return name.endsWith("_demo") ? name.slice(0, -5) : name;
}

function fileNameFromId(videoId) {
  if (!videoId) return "";
  const parts = videoId.split("/");
  return parts[parts.length - 1];
}

// ====== 收集当前页数据；未完成返回 false ======
function collectCurrentPage() {
  const start = idx * GROUP_SIZE;
  const groupVideos = videoList.slice(start, start + GROUP_SIZE);
  const current = { orders: {} };
  for (const c of CRITERIA) {
    const orderIdxs = currentOrders[c.key];
    if (!orderIdxs || orderIdxs.length !== groupVideos.length) return false;
    current.orders[c.key] = orderIdxs.map((i) => groupVideos[i].id ?? `idx_${i}`);
  }
  ratings[idx] = current;
  return true;
}

// ====== 导出 CSV ======
function downloadCSV() {
  const header = ["group_index", "label", "model_name", "video_id", "SA_rank", "PC_rank", "VQ_rank"];
  const lines = [header.join(",")];
  ratings.forEach((r, gIdx) => {
    const start = gIdx * GROUP_SIZE;
    const groupVideos = videoList.slice(start, start + GROUP_SIZE);
    const ranksByCriterion = {};
    CRITERIA.forEach((c) => {
      const order = r.orders?.[c.key] || [];
      order.forEach((vid, pos) => {
        ranksByCriterion[vid] = ranksByCriterion[vid] || {};
        ranksByCriterion[vid][shortName(c.key)] = pos + 1;
      });
    });

    groupVideos.forEach((v, idxInGroup) => {
      const label = displayLabel(idxInGroup);
      const ranks = ranksByCriterion[v.id] || {};
      const row = [
        gIdx + 1,
        label,
        modelNameFromId(v.id),
        fileNameFromId(v.id),
        ranks["SA_rank"] ?? "",
        ranks["PC_rank"] ?? "",
        ranks["VQ_rank"] ?? "",
      ];
      lines.push(row.join(","));
    });
  });
  const blob = new Blob([lines.join("\n")], { type: "text/csv;charset=utf-8" });
  const url = URL.createObjectURL(blob);
  const a = document.createElement("a");
  a.href = url;
  a.download = "user_study.csv";
  a.click();
  URL.revokeObjectURL(url);
}
