// 评分项定义（键、中文名）
const CRITERIA = [
  { key: "semanticAdherence", label: "语义贴合度 (Semantic Adherence)" },
  { key: "physicalCommonsense", label: "物理常识性 (Physical Commonsense)" },
  { key: "videoQuality", label: "视频质量 (Video Quality)" },
];

// ====== 状态 ======
let idx = 0; // 当前视频索引
// ratings: [{videoId, semanticAdherence, physicalCommonsense, videoQuality}]
const ratings = Array.from({ length: videoList.length }, () => ({}));

// ====== DOM 引用 ======
const cover = document.getElementById("cover");
const page = document.getElementById("page");
const done = document.getElementById("done");
const startBtn = document.getElementById("startBtn");
const backBtn = document.getElementById("backBtn");
const nextBtn = document.getElementById("nextBtn");
const downloadBtn = document.getElementById("downloadBtn");
const counter = document.getElementById("counter");
const videoEl = document.getElementById("video");
const videoTitle = document.getElementById("videoTitle");
const promptImage = document.getElementById("promptImage");
const formArea = document.getElementById("formArea");
const errorBox = document.getElementById("errorBox");

// ====== 事件绑定 ======
startBtn.addEventListener("click", () => {
  if (!videoList.length) {
    alert("videoList 为空，请先运行 process.py 生成列表。");
    return;
  }
  idx = 0;
  renderPage();
});

backBtn.addEventListener("click", () => {
  if (idx > 0) {
    collectCurrentPage(); // 允许未满也保存，方便返回修正
    idx--;
    renderPage();
  }
});

nextBtn.addEventListener("click", () => {
  if (!collectCurrentPage()) {
    errorBox.style.display = "block";
    return;
  }
  if (idx < videoList.length - 1) {
    idx++;
    renderPage();
  } else {
    // 完成
    page.style.display = "none";
    done.style.display = "block";
  }
});

downloadBtn.addEventListener("click", downloadCSV);

// ====== 渲染单页 ======
function renderPage() {
  cover.style.display = "none";
  done.style.display = "none";
  page.style.display = "block";

  const N = videoList.length;
  counter.textContent = `第 ${idx + 1} / ${N} 条`;
  videoTitle.textContent = `视频 ${idx + 1}`;
  videoEl.src = videoList[idx].src;
  videoEl.load();
  loadPrompt(videoList[idx]);

  // 渲染 5 个评分项
  const saved = ratings[idx];
  formArea.innerHTML = "";
  CRITERIA.forEach((c) => {
    const q = document.createElement("div");
    const title = document.createElement("div");
    title.innerHTML = `<strong>${c.label}</strong>`;
    const group = makeRadioGroup(c.key, saved[c.key]);
    q.appendChild(title);
    q.appendChild(group);
    q.style.marginBottom = "10px";
    formArea.appendChild(q);
  });

  // 按钮 & 错误提示
  backBtn.style.visibility = idx === 0 ? "hidden" : "visible";
  nextBtn.textContent = idx === N - 1 ? "提交并完成" : "下一条";
  errorBox.style.display = "none";
}

// ====== 加载提示词与图片 ======
async function loadPrompt(video) {
  promptImage.style.display = "none";

  if (video.promptImg) {
    promptImage.src = video.promptImg;
    promptImage.style.display = "block";
  }
}

// ====== 工具：生成 1~5 单选 ======
function makeRadioGroup(name, saved) {
  const wrap = document.createElement("div");
  wrap.className = "radio-group";
  for (let v = 1; v <= 5; v++) {
    const id = `${name}-${v}`;
    const label = document.createElement("label");
    const input = document.createElement("input");
    input.type = "radio";
    input.name = name; // 单页唯一
    input.value = String(v);
    input.id = id;
    if (saved && String(saved) === String(v)) input.checked = true;
    const span = document.createElement("span");
    span.textContent = v;
    label.appendChild(input);
    label.appendChild(span);
    wrap.appendChild(label);
  }
  return wrap;
}

// ====== 收集当前页数据；未完成返回 false ======
function collectCurrentPage() {
  const current = { videoId: videoList[idx].id ?? `index_${idx}` };
  for (const c of CRITERIA) {
    const chosen = document.querySelector(`input[name="${c.key}"]:checked`);
    if (!chosen) return false;
    current[c.key] = Number(chosen.value);
  }
  ratings[idx] = current;
  return true;
}

// ====== 导出 CSV ======
function downloadCSV() {
  const header = ["video_id", ...CRITERIA.map((c) => c.key)];
  const lines = [header.join(",")];
  ratings.forEach((r) => {
    const row = [r.videoId, ...CRITERIA.map((c) => r[c.key])];
    lines.push(row.join(","));
  });
  const blob = new Blob([lines.join("\n")], { type: "text/csv;charset=utf-8" });
  const url = URL.createObjectURL(blob);
  const a = document.createElement("a");
  a.href = url;
  a.download = "user_study.csv";
  a.click();
  URL.revokeObjectURL(url);
}
